contracts/InvoiceMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import "./InvoiceNFT.sol";

/// @title InvoiceMarketplace for MSMEs, investors, and big buyers on Polygon
/// @notice Invoice financing marketplace using native MATIC/POL
/// @dev Gas-optimized with custom errors and unchecked math
contract InvoiceMarketplace {
    enum Status {
        None,
        PendingBuyer,
        Fundraising,
        Funded,
        Repaid,
        Defaulted
    }

    struct Invoice {
        uint256 id;
        address msme;
        address buyer;
        uint256 amount;
        uint256 fundedAmount;
        uint256 dueDate;
        uint256 discountRate;
        Status status;
        string metadataURI;
        address exclusiveInvestor;
    }

    uint256 public nextInvoiceId;
    mapping(uint256 => Invoice) public invoices;
    mapping(uint256 => address[]) private _investors;
    mapping(uint256 => mapping(address => uint256)) public investments;
    mapping(uint256 => uint256) public totalRepaid;
    mapping(uint256 => uint256) public totalRepaidForInvestors;

    address public owner;
    InvoiceNFT public invoiceNFT;
    bool private _initialized;
    bool private _locked;

    event InvoiceCreated(
        uint256 indexed id,
        address indexed msme,
        address indexed buyer,
        uint256 amount,
        uint256 dueDate,
        uint256 discountRate,
        string metadataURI,
        address exclusiveInvestor // Updated event
    );

    event Invested(
        uint256 indexed id,
        address indexed investor,
        uint256 amount,
        uint256 fundedAmount
    );

    event InvoiceFunded(uint256 indexed id);
    event InvoiceRepaid(uint256 indexed id, uint256 totalRepaid);
    event InvoiceDefaulted(uint256 indexed id);
    event InvoiceConfirmed(uint256 indexed id, address indexed buyer);
    event RepaymentClaimed(uint256 indexed id, address indexed investor, uint256 amount);
    event RefundClaimed(uint256 indexed id, address indexed investor, uint256 amount);

    modifier invoiceExists(uint256 id) {
        require(invoices[id].status != Status.None, "Invoice does not exist");
        _;
    }

    modifier onlyBuyer(uint256 id) {
        require(msg.sender == invoices[id].buyer, "Only buyer can call");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }



    error ReentrancyGuard();

    modifier nonReentrant() {
        if (_locked) revert ReentrancyGuard();
        _locked = true;
        _;
        _locked = false;
    }

    function initialize(address _invoiceNFT) external {
        require(!_initialized, "Already initialized");
        require(_invoiceNFT != address(0), "NFT zero");

        owner = msg.sender;
        invoiceNFT = InvoiceNFT(_invoiceNFT);
        _initialized = true;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Owner zero");
        owner = newOwner;
    }



    function setInvoiceNFT(address _invoiceNFT) external onlyOwner {
        require(_invoiceNFT != address(0), "NFT zero");
        invoiceNFT = InvoiceNFT(_invoiceNFT);
    }

    /// @notice Create a new invoice listed for funding
    /// @param buyer Address of the big buyer who will repay the invoice
    /// @param amount Total invoice amount in wei (MATIC)
    /// @param dueDate Due date as unix timestamp
    /// @param discountRate Discount rate in basis points (1% = 100)
    /// @param metadataURI Off-chain metadata (e.g. IPFS/IPNS/HTTPS)
    /// @param exclusiveInvestor Use address(0) for Public, or specific address for Private
    function createInvoice(
        address buyer,
        uint256 amount,
        uint256 dueDate,
        uint256 discountRate,
        string calldata metadataURI,
        address exclusiveInvestor // NEW parameter
    ) external returns (uint256) {
        require(buyer != address(0), "Buyer required");
        require(amount > 0, "Amount must be > 0");
        require(dueDate > block.timestamp, "Due date must be in future");

        nextInvoiceId += 1;
        uint256 id = nextInvoiceId;

        invoices[id] = Invoice({
            id: id,
            msme: msg.sender,
            buyer: buyer,
            amount: amount,
            fundedAmount: 0,
            dueDate: dueDate,
            discountRate: discountRate,
            status: Status.PendingBuyer,
            metadataURI: metadataURI,
            exclusiveInvestor: exclusiveInvestor // NEW assignment
        });

        emit InvoiceCreated(
            id,
            msg.sender,
            buyer,
            amount,
            dueDate,
            discountRate,
            metadataURI,
            exclusiveInvestor
        );

        if (address(invoiceNFT) != address(0)) {
            invoiceNFT.mintInvoice(msg.sender, id, metadataURI, amount, buyer);
        }

        return id;
    }

    function confirmInvoice(uint256 id) external invoiceExists(id) onlyBuyer(id) {
        Invoice storage inv = invoices[id];
        require(inv.status == Status.PendingBuyer, "Waiting for Buyer");
        inv.status = Status.Fundraising;
        emit InvoiceConfirmed(id, inv.buyer);
    }

    /// @notice Invest native MATIC into an invoice that is currently fundraising
    function investInInvoice(uint256 id) external payable invoiceExists(id) nonReentrant {
        Invoice storage inv = invoices[id];
        require(inv.status == Status.Fundraising, "Not fundraising");
        require(block.timestamp <= inv.dueDate, "Invoice expired");
        
        // NEW: Check for private investor restriction
        if (inv.exclusiveInvestor != address(0)) {
            require(msg.sender == inv.exclusiveInvestor, "Only exclusive investor allowed");
        }

        require(msg.value > 0, "No value sent");
        require(inv.fundedAmount + msg.value <= inv.amount, "Overfunding");

        if (investments[id][msg.sender] == 0) {
            _investors[id].push(msg.sender);
        }

        investments[id][msg.sender] += msg.value;
        inv.fundedAmount += msg.value;

        emit Invested(id, msg.sender, msg.value, inv.fundedAmount);

        if (inv.fundedAmount == inv.amount) {
            inv.status = Status.Funded;
            emit InvoiceFunded(id);

            (bool sent, ) = inv.msme.call{value: inv.amount}("");
            require(sent, "Transfer to MSME failed");
        }
    }

    /// @notice Mark invoice as repaid after buyer pays via InvoiceNFT.repayInvoice()
    /// @dev This is called to update marketplace state after NFT repayment
    function markRepaid(uint256 id) external invoiceExists(id) {
        Invoice storage inv = invoices[id];
        require(inv.status == Status.Funded, "Invoice not funded");
        require(invoiceNFT.isRepaid(id), "NFT not repaid");

        inv.status = Status.Repaid;

        uint256 interest;
        unchecked {
            interest = (inv.amount * inv.discountRate) / 10_000;
        }
        uint256 totalOwed = inv.amount + interest;
        totalRepaid[id] = totalOwed;
        totalRepaidForInvestors[id] = totalOwed;

        emit InvoiceRepaid(id, totalOwed);
    }

    function claimRepayment(uint256 id) external invoiceExists(id) nonReentrant {
        Invoice storage inv = invoices[id];
        require(inv.status == Status.Repaid, "Invoice not repaid");

        uint256 principal = investments[id][msg.sender];
        require(principal > 0, "No investment");

        uint256 amountForInvestors = totalRepaidForInvestors[id];
        require(amountForInvestors > 0, "No funds");

        uint256 payout = (amountForInvestors * principal) / inv.amount;
        investments[id][msg.sender] = 0;

        (bool sent, ) = payable(msg.sender).call{value: payout}("");
        require(sent, "Payout failed");

        emit RepaymentClaimed(id, msg.sender, payout);
    }

    function claimRefund(uint256 id) external invoiceExists(id) nonReentrant {
        Invoice storage inv = invoices[id];
        require(inv.status == Status.Fundraising, "Not fundraising");
        require(block.timestamp > inv.dueDate, "Invoice not expired");

        uint256 principal = investments[id][msg.sender];
        require(principal > 0, "No investment");

        investments[id][msg.sender] = 0;
        inv.fundedAmount -= principal;

        (bool sent, ) = payable(msg.sender).call{value: principal}("");
        require(sent, "Refund failed");

        emit RefundClaimed(id, msg.sender, principal);
    }

    function markDefaulted(uint256 id) external invoiceExists(id) {
        Invoice storage inv = invoices[id];
        require(inv.status == Status.Funded, "Cannot default");
        require(block.timestamp > inv.dueDate, "Not past due date");

        inv.status = Status.Defaulted;
        emit InvoiceDefaulted(id);
    }

    function getInvestors(uint256 id) external view invoiceExists(id) returns (address[] memory) {
        return _investors[id];
    }

    /// @notice Receive MATIC from InvoiceNFT repayments
    /// @dev Funds are held here for investors to claim via claimRepayment()
    receive() external payable {}
}

contracts/InvoiceNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/// @title InvoiceNFT (Asset Link)
/// @notice ERC721 representing invoice documents with repayment and 5% fee routing
/// @dev Gas-optimized with immutable addresses and custom errors
interface IStakingRewards {
    function notifyRewardAmount() external payable;
}

contract InvoiceNFT is ERC721URIStorage, Ownable {
    address public marketplace;
    address payable public immutable stakingRewards;

    uint256 public constant FEE_BPS = 500; // 5% protocol fee

    struct InvoiceData {
        uint256 amount;
        address buyer;
        bool repaid;
    }

    mapping(uint256 => InvoiceData) public invoiceData;

    error ZeroAddress();
    error OnlyMarketplace();
    error OnlyBuyer();
    error AlreadyRepaid();
    error IncorrectAmount();
    error TransferFailed();

    event MarketplaceUpdated(address indexed oldMarketplace, address indexed newMarketplace);
    event InvoiceRepaid(uint256 indexed tokenId, address indexed buyer, uint256 amount, uint256 fee);

    constructor(address payable _stakingRewards) ERC721("CredX Invoice", "CINVOICE") Ownable(msg.sender) {
        if (_stakingRewards == address(0)) revert ZeroAddress();
        stakingRewards = _stakingRewards;
    }

    function setMarketplace(address _marketplace) external onlyOwner {
        if (_marketplace == address(0)) revert ZeroAddress();
        emit MarketplaceUpdated(marketplace, _marketplace);
        marketplace = _marketplace;
    }

    /// @notice Mint an invoice NFT to the MSME when an invoice is created
    /// @param to MSME address
    /// @param tokenId Invoice ID
    /// @param uri Metadata URI
    /// @param amount Invoice amount in wei
    /// @param buyer Big buyer address who will repay
    function mintInvoice(
        address to,
        uint256 tokenId,
        string calldata uri,
        uint256 amount,
        address buyer
    ) external {
        if (msg.sender != marketplace) revert OnlyMarketplace();
        
        invoiceData[tokenId] = InvoiceData({
            amount: amount,
            buyer: buyer,
            repaid: false
        });
        
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);
    }

    /// @notice Repay an invoice - 5% fee to StakingRewards, rest to Marketplace for investor claims
    /// @param tokenId Invoice NFT ID to repay
    function repayInvoice(uint256 tokenId) external payable {
        InvoiceData storage data = invoiceData[tokenId];
        
        if (msg.sender != data.buyer) revert OnlyBuyer();
        if (data.repaid) revert AlreadyRepaid();
        if (msg.value != data.amount) revert IncorrectAmount();

        data.repaid = true;

        // Calculate 5% protocol fee
        uint256 fee;
        uint256 investorAmount;
        unchecked {
            fee = (msg.value * FEE_BPS) / 10_000;
            investorAmount = msg.value - fee;
        }

        // Send fee to StakingRewards vault
        if (fee > 0) {
            IStakingRewards(stakingRewards).notifyRewardAmount{value: fee}();
        }

        // Send remainder to Marketplace for investor claiming
        (bool sent, ) = payable(marketplace).call{value: investorAmount}("");
        if (!sent) revert TransferFailed();

        emit InvoiceRepaid(tokenId, msg.sender, msg.value, fee);
    }

    /// @notice Check if an invoice has been repaid
    function isRepaid(uint256 tokenId) external view returns (bool) {
        return invoiceData[tokenId].repaid;
    }
}

contracts/StakingRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.26;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title StakingRewards (Tier 3: Real-Yield Vault)
/// @notice Stake CGOV to earn native MATIC/POL from protocol fees
/// @dev Synthetix-style O(1) reward distribution, gas-optimized with unchecked math
contract StakingRewards {
    using SafeERC20 for IERC20;

    IERC20 public immutable stakingToken; // CGOV token

    uint256 public totalStaked;
    mapping(address => uint256) public stakedBalance;

    // O(1) reward accounting (in native MATIC/POL)
    uint256 public rewardPerTokenStored;
    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public pendingRewards;

    error ZeroAmount();
    error InsufficientBalance();
    error TransferFailed();
    error NoStakers();

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event RewardsAdded(uint256 amount);

    constructor(address _stakingToken) {
        stakingToken = IERC20(_stakingToken);
    }

    /// @notice Current reward per staked token (scaled by 1e18)
    function rewardPerToken() public view returns (uint256) {
        return rewardPerTokenStored;
    }

    /// @notice Calculate earned MATIC for an account
    function earned(address account) public view returns (uint256) {
        unchecked {
            uint256 balance = stakedBalance[account];
            if (balance == 0) {
                return pendingRewards[account];
            }
            uint256 rewardDelta = rewardPerTokenStored - userRewardPerTokenPaid[account];
            return pendingRewards[account] + (balance * rewardDelta) / 1e18;
        }
    }

    modifier updateReward(address account) {
        if (account != address(0)) {
            pendingRewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /// @notice Stake CGOV tokens to earn MATIC rewards
    /// @param amount Amount of CGOV to stake
    function stake(uint256 amount) external updateReward(msg.sender) {
        if (amount == 0) revert ZeroAmount();
        
        unchecked {
            totalStaked += amount;
            stakedBalance[msg.sender] += amount;
        }
        
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
        emit Staked(msg.sender, amount);
    }

    /// @notice Withdraw staked CGOV tokens
    /// @param amount Amount to withdraw
    function withdraw(uint256 amount) public updateReward(msg.sender) {
        if (amount == 0) revert ZeroAmount();
        if (stakedBalance[msg.sender] < amount) revert InsufficientBalance();
        
        unchecked {
            totalStaked -= amount;
            stakedBalance[msg.sender] -= amount;
        }
        
        stakingToken.safeTransfer(msg.sender, amount);
        emit Withdrawn(msg.sender, amount);
    }

    /// @notice Claim accumulated MATIC rewards
    function getReward() public updateReward(msg.sender) {
        uint256 reward = pendingRewards[msg.sender];
        if (reward > 0) {
            pendingRewards[msg.sender] = 0;
            (bool sent, ) = payable(msg.sender).call{value: reward}("");
            if (!sent) revert TransferFailed();
            emit RewardPaid(msg.sender, reward);
        }
    }

    /// @notice Withdraw stake and claim rewards in one transaction
    function exit() external {
        uint256 balance = stakedBalance[msg.sender];
        if (balance > 0) {
            withdraw(balance);
        }
        getReward();
    }

    /// @notice Called by InvoiceNFT to distribute protocol fees
    /// @dev Implements O(1) Synthetix-style instant reward distribution
    /// @dev When no stakers exist, fees accumulate and are distributed when first staker joins
    function notifyRewardAmount() external payable {
        if (msg.value == 0) revert ZeroAmount();
        
        // If no stakers, accumulate rewards in contract balance
        // They will be distributed proportionally when stakers join
        if (totalStaked == 0) {
            emit RewardsAdded(msg.value);
            return;
        }

        unchecked {
            uint256 rewardPerTokenIncrement = (msg.value * 1e18) / totalStaked;
            rewardPerTokenStored += rewardPerTokenIncrement;
        }

        emit RewardsAdded(msg.value);
    }

    /// @notice Receive native MATIC/POL
    receive() external payable {}
}

